import React, { useState } from "react";
import { View, Text, StyleSheet, ToastAndroid } from "react-native";

import { AuthContext } from "../../components/context";

import Header from "../../components/Header";
import { theme } from "../../Core/Theme";
import TextInput from "../../components/TextInput";
import Background from "../../components/Background";
import Button from "../../components/Button";

export default function PostJob({ navigation }) {
    const { getUserToken } = React.useContext(AuthContext);
    const UserToken = getUserToken();

    const [JobTitle, setJobTitle] = useState({ value: "", error: "" });
    const [JobDetails, setJobDetails] = useState({ value: "", error: "" });

    const onSubmitPressed = () => {
        fetch("http://223.16.12.55:3000/postJob", {
            headers: {
                UserToken: UserToken,
                JobTitle: JobTitle.value,
                JobDetails: JobDetails.value,
            },
        }).then((response) => {
            if (response.status === 200) {
                response.json().then((json) => {
                    if (json.success) {
                        ToastAndroid.show("Post Success!", ToastAndroid.SHORT);
                        navigation.navigate("JobList");
                    } else {
                        console.log(json);
                        console.log("failed!");
                    }
                });
            } else {
                setJobDetails({
                    ...JobDetails,
                    error: "Server currently not available",
                });
            }
        });
        console.log("Pressed");
    };

    return (
        <Background>
            <Header>Post a New Job</Header>
            <TextInput
                label="Title"
                returnKeyType="next"
                value={JobTitle.value}
                onChangeText={(text) => setJobTitle({ value: text, error: "" })}
                error={!!JobTitle.error}
                errorText={JobTitle.error}
            />
            <TextInput
                label="Details"
                returnKeyType="done"
                value={JobDetails.value}
                onChangeText={(text) =>
                    setJobDetails({ value: text, error: "" })
                }
                error={!!JobDetails.error}
                errorText={JobDetails.error}
            />
            <Button mode="contained" onPress={onSubmitPressed}>
                Submit
            </Button>
        </Background>
    );
}

const styles = StyleSheet.create({
    forgotPassword: {
        width: "100%",
        alignItems: "flex-end",
        marginBottom: 24,
    },
    row: {
        flexDirection: "row",
        marginTop: 4,
    },
    forgot: {
        fontSize: 13,
        color: theme.colors.secondary,
    },
    link: {
        fontWeight: "bold",
        color: theme.colors.primary,
    },
});
