import * as React from "react";
import {
    View,
    Text,
    StyleSheet,
    Button,
    Alert,
    Linking,
    SafeAreaView,
    ScrollView,
    Pressable,
} from "react-native";
import { useEffect, useState } from "react";
import { useNavigation } from "@react-navigation/native";
import Toast from "react-native-simple-toast";
import Ionicons from "react-native-vector-icons/Ionicons";

import { AuthContext } from "../../components/context";

export default function JobDetails(item) {
    const navigation = useNavigation();
    const { getUserToken, getUserData, getAPIServiceURL } =
        React.useContext(AuthContext);
    const UserToken = getUserToken();
    const UserData = getUserData();
    const APIServiceURL = getAPIServiceURL();
    const [showDelButton, setShowDelButton] = useState(false);
    const [followingCurrentJob, setFollowingCurrentJob] = useState(false);
    let fetching = false;
    let job = item.route.params;

    const FollowingJob = () => {
        if (fetching) {
            return;
        }
        fetching = true;
        if (followingCurrentJob) {
            fetch(`${APIServiceURL}/unfollowJob`, {
                headers: {
                    user_id: UserData._id,
                    job_id: job._id,
                },
            })
                .then((response) => {
                    if (response.status === 200) {
                        response.json().then((json) => {
                            if (json.success) {
                                setFollowingCurrentJob(false);
                                Toast.show("Unfollowed!", Toast.SHORT);
                            } else {
                                console.log("failed!");
                            }
                        });
                    }
                })
                .finally(() => {
                    fetching = false;
                });
        } else {
            fetching = true;
            fetch(`${APIServiceURL}/followJob`, {
                headers: {
                    user_id: UserData._id,
                    job_id: job._id,
                },
            })
                .then((response) => {
                    if (response.status === 200) {
                        response.json().then((json) => {
                            if (json.success) {
                                setFollowingCurrentJob(true);
                                Toast.show("Followed!", Toast.SHORT);
                            } else {
                                console.log("failed!");
                            }
                        });
                    }
                })
                .finally(() => {
                    fetching = false;
                });
        }
    };

    const checkFollowing = async () => {
        fetching = true;
        fetch(`${APIServiceURL}/checkFollowing`, {
            headers: {
                user_id: UserData._id,
                job_id: job._id,
            },
        })
            .then((response) => {
                if (response.status === 200) {
                    response.json().then((json) => {
                        if (json.success) {
                            setFollowingCurrentJob(true);
                        } else {
                            setFollowingCurrentJob(false);
                        }
                    });
                } else {
                    setFollowingCurrentJob(false);
                }
            })
            .finally(() => {
                fetching = false;
            });
    };

    useEffect(async () => {
        const User = await JobAuthor(UserToken);
        if (User === job.Author) {
            setShowDelButton(true);
        }
        checkFollowing();
        navigation.setOptions({
            title: decodeURIComponent(decodeURIComponent(job.Title)),
            // headerLeft: null,
            headerRight: () => <FollowingIconButton navigation={navigation} />,
        });
    });

    const getJobAuthorEmailByAuthorID = async (author_id) => {
        return new Promise((resolve, reject) => {
            fetch(`${APIServiceURL}/getJobAuthorEmail`, {
                headers: {
                    author_id: author_id,
                },
            }).then((response) => {
                if (response.status === 200) {
                    response.json().then((json) => {
                        if (json.success) {
                            resolve(json.email);
                        } else {
                            reject(json.success);
                        }
                    });
                } else {
                    reject(false);
                }
            });
        });
    };

    const FollowingIconButton = ({ navigation }) => {
        if (followingCurrentJob) {
            return (
                <Ionicons
                    name="star"
                    size={35}
                    onPress={() => {
                        FollowingJob();
                    }}
                />
            );
        } else {
            return (
                <Ionicons
                    name="star-outline"
                    size={35}
                    onPress={() => {
                        FollowingJob();
                    }}
                />
            );
        }
    };

    //#TODO: Add Job Modification Functionality if Author is the same as the logged in user

    //#DONE: Add Job follow/unfollow functionality

    //#TODO: Add Button to Add Notes for current job offer

    const confirmPopUp = () => {
        Alert.alert("Deleting Job", "Are you sure?", [
            {
                text: "Cancel",
            },
            {
                text: "I'm sure",
                onPress: () => {
                    deleteJob();
                },
            },
        ]);
    };
    const deleteJob = () => {
        fetch(`${APIServiceURL}/deleteJob`, {
            headers: {
                jobid: job._id,
            },
        }).then((response) => {
            if (response.status === 200) {
                response.json().then((json) => {
                    if (json.success) {
                        Toast.show("Delete Success!", Toast.SHORT);
                        navigation.navigate("JobList");
                    } else {
                        Toast.show("Delete Failed!", Toast.SHORT);
                    }
                });
            } else {
                Toast.show("Delete Failed!", Toast.SHORT);
            }
        });
    };
    const JobAuthor = async (UserToken) => {
        return new Promise((resolve, reject) => {
            fetch(`${APIServiceURL}/getUser`, {
                headers: {
                    usertoken: UserToken,
                },
            }).then((response) => {
                if (response.status === 200) {
                    response.json().then((json) => {
                        if (json.success) {
                            resolve(json.data.username);
                        } else {
                            reject(json.success);
                        }
                    });
                } else {
                    reject(false);
                }
            });
        });
    };

    return (
        <SafeAreaView>
            <ScrollView>
                <Text
                    style={{
                        fontSize: 20,
                        fontWeight: "bold",
                        textAlign: "center",
                    }}
                >
                    {`${decodeURIComponent(job.Title)}`}
                </Text>
                <Text
                    style={{
                        fontSize: 16,
                        fontWeight: "bold",
                        textAlign: "center",
                    }}
                >
                    {`Job Provider: ${decodeURIComponent(job.Author)}`}
                </Text>
                <Text
                    style={{
                        fontSize: 26,
                        fontWeight: "bold",
                    }}
                >
                    {`${decodeURIComponent(job.Details)}`}
                </Text>
                <View style={styles.buttonView}>
                    {showDelButton ? (
                        <Pressable
                            style={styles.delButton}
                            onPress={() => {
                                confirmPopUp();
                            }}
                        >
                            <Text style={styles.buttonText}>üóëÔ∏è</Text>
                        </Pressable>
                    ) : (
                        <Pressable
                            onPress={async () =>
                                Linking.openURL(
                                    `mailto:${await getJobAuthorEmailByAuthorID(
                                        job.AuthorID
                                    )}`
                                )
                            }
                            style={styles.emailButton}
                        >
                            <Text style={styles.buttonText}>üìÆ</Text>
                        </Pressable>
                    )}

                    <Pressable
                        onPress={() => {
                            navigation.navigate(
                                "Notes List",
                                { navigation },
                                job
                            );
                        }}
                        style={styles.forwardsButton}
                    >
                        <Text style={styles.buttonText}>Forward To Notes</Text>
                    </Pressable>
                </View>
            </ScrollView>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    center: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
    },
    button: {
        margin: 5,
        padding: 10,
        paddingLeft: 20,
        paddingRight: 20,
        backgroundColor: "#406E9F",
        borderRadius: 15,
        alignItems: "center",
        justifyContent: "center",
    },
    buttonText: {
        color: "#fff",
        fontSize: 15,
        fontWeight: "bold",
    },

    delButton: {
        margin: 1,
        padding: 10,
        paddingLeft: 20,
        paddingRight: 20,
        backgroundColor: "red",
        borderRadius: 15,
        alignItems: "center",
        justifyContent: "center",
    },
    forwardsButton: {
        margin: 5,
        padding: 10,
        paddingLeft: 20,
        paddingRight: 20,
        backgroundColor: "green",
        borderRadius: 15,
        alignItems: "center",
        justifyContent: "center",
    },
    emailButton: {
        margin: 5,
        padding: 10,
        paddingLeft: 20,
        paddingRight: 20,
        backgroundColor: "purple",
        borderRadius: 15,
        alignItems: "center",
        justifyContent: "center",
    },
    buttonView: {
        flexDirection: "row",
        justifyContent: "space-around",
        alignItems: "center",
        marginTop: 20,
    },
});
