import * as React from "react";
import { View, Text } from "react-native";

export default function JobDetails(item) {
    let job = item.route.params;
    return (
        <View
            style={{ flex: 1, alignItems: "center", justifyContent: "center" }}
        >
            <Text
                style={{
                    fontSize: 26,
                    fontWeight: "bold",
                    textAlign: "center",
                }}
            >
                {`${job.Title}`}
            </Text>
            <Text
                style={{
                    fontSize: 26,
                    fontWeight: "bold",
                    textAlign: "center",
                }}
            >
                {`${job.Author}`}
            </Text>
            <Text
                style={{
                    fontSize: 26,
                    fontWeight: "bold",
                    textAlign: "center",
                }}
            >
                {`${job.Details}`}
            </Text>
        </View>
    );
}
