import * as React from "react";
import {
    View,
    Text,
    StyleSheet,
    Button,
    Alert,
    Linking,
    SafeAreaView,
    ScrollView,
    Pressable,
    ToastAndroid,
} from "react-native";
import { useEffect, useState } from "react";
import { useNavigation } from "@react-navigation/native";
import Ionicons from "react-native-vector-icons/Ionicons";

import { AuthContext } from "../../components/context";

export default function JobDetails(item) {
    const navigation = useNavigation();
    const { getUserToken, getUserData, getAPIServiceURL, setJob } =
        React.useContext(AuthContext);
    const UserToken = getUserToken();
    const UserData = getUserData();
    const APIServiceURL = getAPIServiceURL();
    const [showDelButton, setShowDelButton] = useState(false);
    const [followingCurrentJob, setFollowingCurrentJob] = useState(false);
    let fetching = false;
    var job = item.route.params;

    const FollowingJob = () => {
        if (fetching) {
            return;
        }
        fetching = true;
        if (followingCurrentJob) {
            fetch(`${APIServiceURL}/unfollowJob`, {
                headers: {
                    user_id: UserData._id,
                    job_id: job._id,
                },
            })
                .then((response) => {
                    if (response.status === 200) {
                        response.json().then((json) => {
                            if (json.success) {
                                setFollowingCurrentJob(false);
                                ToastAndroid.show(
                                    "Unfollowed!",
                                    ToastAndroid.SHORT
                                );
                            } else {
                                console.log("failed!");
                            }
                        });
                    }
                })
                .finally(() => {
                    fetching = false;
                });
        } else {
            fetching = true;
            fetch(`${APIServiceURL}/followJob`, {
                headers: {
                    user_id: UserData._id,
                    job_id: job._id,
                },
            })
                .then((response) => {
                    if (response.status === 200) {
                        response.json().then((json) => {
                            if (json.success) {
                                setFollowingCurrentJob(true);
                                ToastAndroid.show(
                                    "Followed!",
                                    ToastAndroid.SHORT
                                );
                            } else {
                                console.log("failed!");
                            }
                        });
                    }
                })
                .finally(() => {
                    fetching = false;
                });
        }
    };

    const checkFollowing = async () => {
        fetching = true;
        fetch(`${APIServiceURL}/checkFollowing`, {
            headers: {
                user_id: UserData._id,
                job_id: job._id,
            },
        })
            .then((response) => {
                if (response.status === 200) {
                    response.json().then((json) => {
                        if (json.success) {
                            setFollowingCurrentJob(true);
                        } else {
                            setFollowingCurrentJob(false);
                        }
                    });
                } else {
                    setFollowingCurrentJob(false);
                }
            })
            .finally(() => {
                fetching = false;
            });
    };

    useEffect(async () => {
        const User = await JobAuthor(UserToken);
        if (User === job.Author) {
            setShowDelButton(true);
        }
        checkFollowing();
        navigation.setOptions({
            title: decodeURIComponent(decodeURIComponent(job.Title)),
            // headerLeft: null,
            headerRight: () => <FollowingIconButton navigation={navigation} />,
        });
    });

    const getJobAuthorEmailByAuthorID = async (author_id) => {
        return new Promise((resolve, reject) => {
            fetch(`${APIServiceURL}/getJobAuthorEmail`, {
                headers: {
                    author_id: author_id,
                },
            }).then((response) => {
                if (response.status === 200) {
                    response.json().then((json) => {
                        if (json.success) {
                            resolve(json.email);
                        } else {
                            reject(json.success);
                        }
                    });
                } else {
                    reject(false);
                }
            });
        });
    };

    const FollowingIconButton = ({ navigation }) => {
        if (followingCurrentJob) {
            return (
                <Ionicons
                    name="star"
                    size={35}
                    onPress={() => {
                        FollowingJob();
                    }}
                />
            );
        } else {
            return (
                <Ionicons
                    name="star-outline"
                    size={35}
                    onPress={() => {
                        FollowingJob();
                    }}
                />
            );
        }
    };

    //#TODO: Add Job Modification Functionality if Author is the same as the logged in user

    //#DONE: Add Job follow/unfollow functionality

    //#DONE: Add Button to Add Notes for current job offer

    const confirmPopUp = () => {
        Alert.alert("Deleting Job", "Are you sure?", [
            {
                text: "Cancel",
            },
            {
                text: "I'm sure",
                onPress: () => {
                    deleteJob();
                },
            },
        ]);
    };
    const deleteJob = () => {
        fetch(`${APIServiceURL}/deleteJob`, {
            headers: {
                jobid: job._id,
            },
        }).then((response) => {
            if (response.status === 200) {
                response.json().then((json) => {
                    if (json.success) {
                        ToastAndroid.show(
                            "Delete Success!",
                            ToastAndroid.SHORT
                        );
                        navigation.navigate("JobList");
                    } else {
                        ToastAndroid.show("Delete Failed!", ToastAndroid.SHORT);
                    }
                });
            } else {
                ToastAndroid.show("Delete Failed!", ToastAndroid.SHORT);
            }
        });
    };
    const JobAuthor = async (UserToken) => {
        return new Promise((resolve, reject) => {
            fetch(`${APIServiceURL}/getUser`, {
                headers: {
                    usertoken: UserToken,
                },
            }).then((response) => {
                if (response.status === 200) {
                    response.json().then((json) => {
                        if (json.success) {
                            resolve(json.data.username);
                        } else {
                            reject(json.success);
                        }
                    });
                } else {
                    reject(false);
                }
            });
        });
    };

    const createNewChatRoom = (targetId) => {
        fetch(`${APIServiceURL}/createNewChatRoom`, {
            headers: {
                user_id: UserData._id,
                target_id: targetId,
            },
        }).then((response) => {
            if (response.status === 200) {
                response.json().then((json) => {
                    if (json.success) {
                        console.log(json.insertedId);
                        enterChat(json.insertedId);
                    } else {
                        if (json.data?._id) {
                            enterChat(json.data._id);
                        } else {
                            ToastAndroid.show("Error!", ToastAndroid.SHORT);
                        }
                    }
                });
            } else {
                ToastAndroid.show("Create Chat Failed!", ToastAndroid.SHORT);
            }
        });
    };

    const enterChat = (chatRoomID) => {
        window.CustomVar_forwardChatRoomID = chatRoomID;
        navigation.navigate("Chat");
    };

    return (
        <View style={{ backgroundColor: "#FFFFFF", height: "100%" }}>
            <SafeAreaView>
                <ScrollView style={{ backgroundColor: "transparent" }}>
                    <Text
                        style={{
                            fontSize: 20,
                            fontWeight: "bold",
                            textAlign: "center",
                        }}
                    >
                        {`${decodeURIComponent(job.Title)}`}
                    </Text>
                    <Text
                        style={{
                            fontSize: 16,
                            fontWeight: "bold",
                            textAlign: "center",
                        }}
                    >
                        {`Job Provider: ${decodeURIComponent(job.Author)}`}
                    </Text>
                    <View
                        style={{ flexDirection: "row", alignItems: "center" }}
                    >
                        <View
                            style={{
                                flex: 1,
                                height: 1,
                                backgroundColor: "black",
                            }}
                        />
                        <View>
                            <Text style={{ width: 50, textAlign: "center" }}>
                                Details
                            </Text>
                        </View>
                        <View
                            style={{
                                flex: 1,
                                height: 1,
                                backgroundColor: "black",
                            }}
                        />
                    </View>
                    <Text
                        style={{
                            fontSize: 26,
                            fontWeight: "bold",
                        }}
                    >
                        {`${decodeURIComponent(job.Details)}`}
                    </Text>
                    <View
                        style={{ flexDirection: "row", alignItems: "center" }}
                    >
                        <View
                            style={{
                                flex: 1,
                                height: 1,
                                backgroundColor: "black",
                            }}
                        />
                        <View>
                            <Text style={{ width: 90, textAlign: "center" }}>
                                Control Panel
                            </Text>
                        </View>
                        <View
                            style={{
                                flex: 1,
                                height: 1,
                                backgroundColor: "black",
                            }}
                        />
                    </View>
                    <View style={styles.buttonView}>
                        {showDelButton ? (
                            <Pressable
                                style={styles.delButton}
                                onPress={() => {
                                    confirmPopUp();
                                }}
                            >
                                <Text style={styles.buttonText}>🗑️</Text>
                            </Pressable>
                        ) : (
                            <>
                                <Pressable
                                    onPress={async () => {
                                        createNewChatRoom(job.AuthorID);
                                    }}
                                    style={styles.chatButton}
                                >
                                    <Text style={styles.buttonText}>💬</Text>
                                </Pressable>
                                <Pressable
                                    onPress={async () =>
                                        Linking.openURL(
                                            `mailto:${await getJobAuthorEmailByAuthorID(
                                                job.AuthorID
                                            )}`
                                        )
                                    }
                                    style={styles.emailButton}
                                >
                                    <Text style={styles.buttonText}>📮</Text>
                                </Pressable>
                            </>
                        )}

                        <Pressable
                            onPress={() => {
                                setJob(true, job);
                                navigation.navigate("NotesList");
                            }}
                            style={styles.forwardsButton}
                        >
                            <Text style={styles.buttonText}>
                                Forward To Notes
                            </Text>
                        </Pressable>
                    </View>
                </ScrollView>
            </SafeAreaView>
        </View>
    );
}

const styles = StyleSheet.create({
    center: {
        flex: 1,
        justifyContent: "center",
