import React, { useEffect } from "react";
import {
    Text,
    StyleSheet,
    View,
    SafeAreaView,
    ScrollView,
    Pressable,
    Alert,
    ToastAndroid,
} from "react-native";

import { AuthContext } from "../../components/context";

export default function NoteDetailsScreen({ route, navigation }) {
    const { getJobRedirect, getAPIServiceURL, getUserToken, setJob } =
        React.useContext(AuthContext);
    const Note_ID = route.params._id;
    const { Job, Note } = route.params;
    const APIServiceURL = getAPIServiceURL();
    useEffect(() => {
        if (Job) {
            navigation.setOptions({
                title: decodeURIComponent(Job.Title),
            });
        }
    });

    const confirmPopUp = () => {
        Alert.alert("Deleting Note", "Are you sure?", [
            {
                text: "Cancel",
            },
            {
                text: "I'm sure",
                onPress: () => {
                    deleteNote();
                },
            },
        ]);
    };
    const deleteNote = () => {
        fetch(`${APIServiceURL}/deleteNote`, {
            headers: {
                note_id: Note_ID ? Note_ID : Note._id,
            },
        }).then((response) => {
            if (response.status === 200) {
                response.json().then((json) => {
                    if (json.success) {
                        navigation.navigate("Notes List");
                        ToastAndroid.show(
                            "Successfully Deleted",
                            ToastAndroid.SHORT
                        );
                    } else {
                        Alert.alert("Error", json.message);
                    }
                });
            } else {
                Alert.alert(
                    "Error",
                    "Server currently not available, Please check your connection."
                );
            }
        });
    };

    return (
        <SafeAreaView>
            <ScrollView>
                {Job ? (
                    <Text
                        style={{
                            fontSize: 20,
                            fontWeight: "bold",
                            textAlign: "center",
                        }}
                    >
                        {`${decodeURIComponent(Job.Title)}`}
                    </Text>
                ) : null}
                {Job ? (
                    <Text
                        style={{
                            fontSize: 16,
                            fontWeight: "bold",
                            textAlign: "center",
                        }}
                    >
                        {`Job Provider: ${decodeURIComponent(Job.Author)}`}
                    </Text>
                ) : null}
                {Job ? (
                    <Text
                        style={{
                            fontSize: 20,
                            fontWeight: "bold",
                        }}
                    >
                        {`${decodeURIComponent(Job.Details)}`}
                    </Text>
                ) : null}
                {Job ? (
                    <View
                        style={{ flexDirection: "row", alignItems: "center" }}
                    >
                        <View
                            style={{
                                flex: 1,
                                height: 1,
                                backgroundColor: "black",
                            }}
                        />
                        <View>
                            <Text style={{ width: 50, textAlign: "center" }}>
                                Note
                            </Text>
                        </View>
                        <View
                            style={{
                                flex: 1,
                                height: 1,
                                backgroundColor: "black",
                            }}
                        />
                    </View>
                ) : null}
                <Text style={{ fontSize: 16 }}>
                    {`${
                        Note.Note
                            ? decodeURIComponent(Note.Note)
                            : decodeURIComponent(Note)
                    }`}
                </Text>
                <View style={{ flexDirection: "row", alignItems: "center" }}>
                    <View
                        style={{ flex: 1, height: 1, backgroundColor: "black" }}
                    />
                    <View>
                        <Text style={{ width: 70, textAlign: "center" }}>
                            Note End
                        </Text>
                    </View>
                    <View
                        style={{ flex: 1, height: 1, backgroundColor: "black" }}
                    />
                </View>
                <Pressable
                    style={styles.delButton}
                    onPress={() => {
                        confirmPopUp();
                    }}
                >
                    <Text style={styles.buttonText}>üóëÔ∏è</Text>
                </Pressable>
            </ScrollView>
        </SafeAreaView>
    );
}

//#NOTE: sample route.params object
// Object {
// "Job": Object {
//   "Author": "Cheungwaiyin",
//   "AuthorID": "61d2c4e2cddf3454f88c77d2",
//   "Details": "Abc",
//   "Title": "Abc",
//   "_id": "61d2ccb34fecc0b50fc085ff",
// },
// "Note": Object {
//   "Author": Object {
// "UserToken": "nrkjkkcv2dmksl45cw9uw",
// "_id": "61d2b58fcddf3454f88c77d1",
// "email": "pw860563576@gmail.com",
// "password": "!Zz860563576",
// "username": "pw860563576",
//   },
//   "Job_id": "61d2ccb34fecc0b50fc085ff",
//   "Note": "7",
//   "_id": "61d43d961028f772796f609c",
// },
//   }

const styles = StyleSheet.create({
    center: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
    },
    button: {
        margin: 5,
        padding: 10,
        paddingLeft: 20,
        paddingRight: 20,
        backgroundColor: "#406E9F",
        borderRadius: 15,
        alignItems: "center",
        justifyContent: "center",
    },
    buttonText: {
        color: "#fff",
        fontSize: 15,
        fontWeight: "bold",
    },

    delButton: {
        margin: 1,
        padding: 10,
        paddingLeft: 20,
        paddingRight: 20,
        backgroundColor: "red",
        borderRadius: 15,
        alignItems: "center",
        justifyContent: "center",
    },
    forwardsButton: {
        margin: 5,
        padding: 10,
        paddingLeft: 20,
        paddingRight: 20,
        backgroundColor: "green",
        borderRadius: 15,
        alignItems: "center",
        justifyContent: "center",
    },
    emailButton: {
        margin: 5,
        padding: 10,
        paddingLeft: 20,
        paddingRight: 20,
        backgroundColor: "purple",
        borderRadius: 15,
        alignItems: "center",
        justifyContent: "center",
    },
    buttonView: {
        flexDirection: "row",
        justifyContent: "space-around",
        alignItems: "center",
        marginTop: 20,
    },
});
